mapper.select:
select pictureUser.id,pictureUser.url,pictureUser.picture_type_id,pictureUser.name
from aa_picture_type as pictureType
left join aa_picture_user as pictureUser on pictureType.id=pictureUser.picture_type_id

	exam_user_id = #{examUserId}

	and pictureType.id in

		#{pictureTypeId}

